using JetBrains.Annotations;using UnityEngine;namespace Core{    public static partial class Database    {        public static Vector3? GetDatabaseVector3([NotNull] string key)        {            if (PlayerPrefs.HasKey(key: $"{key}_x") &&                PlayerPrefs.HasKey(key: $"{key}_y") &&                PlayerPrefs.HasKey(key: $"{key}_z"))            {                float x = PlayerPrefs.GetFloat(key: $"{key}_x");                float y = PlayerPrefs.GetFloat(key: $"{key}_y");                float z = PlayerPrefs.GetFloat(key: $"{key}_z");                return new Vector3(x: x, y: y, z: z);            }            return null;        }    }}