using JetBrains.Annotations;using UnityEngine;namespace Core{    public static partial class Database    {        public static Vector3? GetDatabaseVector3([NotNull] string key)        {            if (PlayerPrefs.HasKey($"{key}_x") &&                PlayerPrefs.HasKey($"{key}_y") &&                PlayerPrefs.HasKey($"{key}_z"))            {                float x = PlayerPrefs.GetFloat($"{key}_x");                float y = PlayerPrefs.GetFloat($"{key}_y");                float z = PlayerPrefs.GetFloat($"{key}_z");                return new Vector3(x, y, z);            }            else            {                return null;            }        }    }}