using JetBrains.Annotations;using UnityEngine;namespace Core{    public static partial class Database    {        public static void SetDatabaseVector3([NotNull] string key, Vector3 value)        {            PlayerPrefs.SetFloat($"{key}_x", value.x);            PlayerPrefs.SetFloat($"{key}_y", value.y);            PlayerPrefs.SetFloat($"{key}_z", value.z);        }    }}