using JetBrains.Annotations;using UnityEngine;namespace Core{    public static partial class Database    {        public static void SetDatabaseVector3([NotNull] string key, Vector3 value)        {            PlayerPrefs.SetFloat(key: $"{key}_x", value: value.x);            PlayerPrefs.SetFloat(key: $"{key}_y", value: value.y);            PlayerPrefs.SetFloat(key: $"{key}_z", value: value.z);        }    }}