using System.Collections.Generic;using UnityEngine;namespace Core{    public static class Utilities    {        public static int GetRandomNumberInRange(Vector2Int range, List<int> excludedValues)        {            int val = Random.Range(min: range.x, max: range.y);            if (excludedValues.Contains(item: val) == false)            {                excludedValues.Add(item: val);                return val;            }            return GetRandomNumberInRange(range: range, excludedValues: excludedValues);        }    }}