using System.Collections.Generic;using UnityEngine;namespace Core{    public static partial class Utilities    {        public static int GetRandomNumberInRange(Vector2Int range, List<int> excludedValues)        {            int val = Random.Range(range.x, range.y);            if (excludedValues.Contains(val) == false)            {                excludedValues.Add(val);                return val;            }            else            {                return GetRandomNumberInRange(range, excludedValues);            }        }    }}