using Core;using UnityEngine;using Memory.GamePaths;using Memory.MonoBehaviours;namespace Memory.Logic{    public static partial class Game    {        public static void CreateCards(int pairCount)        {            for (int i = 0; i < pairCount; i++)            {                Sprite animalSprite = _visualSettings.Animals[i];                var tempObj1 = _factory.CreateGameObjectFromResources(path: Paths.MemoryCardPrefabPath);                var card1 = tempObj1.GetComponent<MemoryCard>();                card1.Init(i, _visualSettings.CardFrame, animalSprite);                var details1 = _grid.GetSlotDetails();                card1.transform.position = details1.Position;                card1.transform.localScale = details1.Scale;                SetCardIndex(instanceId: card1.GetInstanceID(), i);                var tempObj2 = _factory.CreateGameObjectFromResources(path: Paths.MemoryCardPrefabPath);                var card2 = tempObj2.GetComponent<MemoryCard>();                card2.Init(i, _visualSettings.CardFrame, animalSprite);                var details2 = _grid.GetSlotDetails();                card2.transform.position = details2.Position;                card2.transform.localScale = details2.Scale;                SetCardIndex(instanceId: card2.GetInstanceID(), i);                card1.Selected += OnCardSelected;                card2.Selected += OnCardSelected;            }        }    }}