using System.Collections.Generic;using Core;using UnityEngine;using Memory.GamePaths;using Memory.MonoBehaviours;namespace Memory.Logic{    public static partial class Game    {        public static void CreateCards(int pairCount)        {            List<int> usedIndexes = new List<int>();            Vector2Int cardIndexRange = new Vector2Int(0, pairCount * 2);            for (int i = 0; i < pairCount * 2; i++)            {                Sprite animalSprite = _visualSettings.Animals[i / 2];                var tempObj1 = _factory.CreateGameObjectFromResources(path: Paths.MemoryCardPrefabPath);                var card1 = tempObj1.GetComponent<MemoryCard>();                int cardIndex = Core.Utilities.GetRandomNumberInRange(cardIndexRange, usedIndexes);                card1.Init(i, _visualSettings.CardFrame, animalSprite);                card1.transform.position = GetCardSlotPosition(cardIndex);                card1.transform.localScale = GetCardSlotScale(cardIndex);                SetCardPairIndex(instanceId: card1.GetInstanceID(), i / 2);                card1.Selected += OnCardSelected;            }        }    }}