using System.Collections.Generic;using Core;using Memory.GamePaths;using Memory.MonoBehaviours;using UnityEngine;namespace Memory.Logic{    public static partial class Game    {        private static void CreateCards(int pairCount)        {            List<int> usedIndexes = new List<int>();            Vector2Int cardIndexRange = new Vector2Int(x: 0, y: pairCount * 2);            for (int i = 0; i < pairCount * 2; i++)            {                int pairIndex = i / 2;                Sprite targetSprite = _gameplaySettings.CarPairs[pairIndex].Sprite;                GameObject tempObj1 = _factory.CreateGameObjectFromResources(path: Paths.MemoryCardPrefabPath);                MemoryCard card1 = tempObj1.GetComponent<MemoryCard>();                int cardIndex = Utilities.GetRandomNumberInRange(range: cardIndexRange, excludedValues: usedIndexes);                                card1.Init(cardIndex: cardIndex,                    pairIndex: pairIndex,                    frame: _visualSettings.CardFrame,                    target: targetSprite,                    _visualSettings.CardFadeDuration);                card1.transform.position = GetCardSlotPosition(index: cardIndex);                card1.transform.localScale = GetCardSlotScale(index: cardIndex);                card1.Selected += OnCardSelected;                card1.CardSelectFlipStarted += OnCardSelectFlipStarted;                card1.CardResetFlipFinished += OnCardResetFlipFinished;                card1.PlayCardSound += OnPlayCardSound;            }        }    }}