using UnityEngine;namespace Memory.Logic{    public static partial class Game    {        private static void OnCardSelected(int pairIndex)        {            Debug.Log(message: $"PAIR CARD SELECTED! INDEX {pairIndex}");            int currentPair = GetCurrentlySelectedPair();            if (currentPair == -1)            {                SetCurrentlySelectedPair(value: pairIndex);            }            else if (currentPair == pairIndex)            {                SetCurrentlySelectedPair(value: -1);                SetMatchedPairs(value: GetMatchedPairs() + 1);                ProgressChanged?.Invoke(arg1: GetMatchedPairs(), arg2: GetCardPairsCount());                PairMatched?.Invoke(obj: pairIndex);                CheckWinCondition();            }            else            {                Debug.Log(message: "Tried to match different cards!");                ResetCards?.Invoke(arg1: currentPair, arg2: pairIndex);                SetCurrentlySelectedPair(value: -1);            }        }    }}