using UnityEngine;namespace Memory.Logic{    public static partial class Game     {        public static void OnCardSelected(int pairIndex)        {            UnityEngine.Debug.Log($"PAIR CARD SELECTED! INDEX {pairIndex}");            int currentPair = GetCurrentlySelectedPair();            if (currentPair == -1)            {                SetCurrentlySelectedPair(pairIndex);            }            else if(currentPair == pairIndex)            {                SetCurrentlySelectedPair(-1);                PairMatched?.Invoke(pairIndex);            }            else            {                Debug.Log("Tried to match different cards!");                ResetCards?.Invoke(currentPair, pairIndex);                SetCurrentlySelectedPair(-1);            }        }    }}