using Memory.Enums;namespace Memory.Logic{    public static partial class Game    {        private static CompletenessType MeasureCompleteness()        {            float secondeLeft = (float) GetTimeLeft().TotalSeconds;            float secondsTotal = (float) GetLevelDuration();            float percent = secondeLeft / secondsTotal;            if (percent > 0.6f)            {                return CompletenessType.PERFECT;            }            if (percent < 0.2f)            {                return CompletenessType.OK;            }            return CompletenessType.GOOD;        }    }}