using System;using Memory.Database;namespace Memory.Logic{    public static partial class Game    {        private static void SetLevelDuration(int value)        {            Core.Database.SetDatabaseInt(key: DatabaseKeys.LevelDurationDatabaseKey, value: value);            LevelDurationSet?.Invoke(obj: DateTime.Now.AddSeconds(value: value).Subtract(value: DateTime.Now));        }    }}