using Memory.Structs;using UnityEngine;using UnityEngine.SceneManagement;namespace Memory.Logic{    public static partial class Game    {        private static void PrepareGameResult()        {            int memoryLevel = Core.Management.Main.GetGameTypeProgress(GameType.MEMORY);            int timeLeft = Mathf.Max(0, (int) GetTimeLeft().TotalSeconds);            float percentage = (float) GetMatchedPairs() / (float) GetCardPairsCount();            MemoryGameResult result = new MemoryGameResult(memoryLevel, timeLeft, percentage);            Core.Management.Main.SetMemoryResult(result);            SceneManager.LoadScene(0);        }    }}