using Core.Management;using Memory.Structs;using UnityEngine;using UnityEngine.SceneManagement;namespace Memory.Logic{    public static partial class Game    {        private static void PrepareGameResult()        {            int memoryLevel = Main.GetGameTypeProgress(type: GameType.MEMORY);            int timeLeft = Mathf.Max(a: 0, b: (int) GetTimeLeft().TotalSeconds);            float percentage = GetMatchedPairs() / (float) GetCardPairsCount();            MemoryGameResult result = new MemoryGameResult(level: memoryLevel, seconds: timeLeft, percentage: percentage);            Main.SetMemoryResult(result: result);        }    }}