using UnityEngine;using Core;using Memory.Logic;namespace Memory.MonoBehaviours{    public class GameBootstrap : MonoBehaviour    {        public void Awake()        {            Game.Awake(FindObjectOfType<Factory>(), FindObjectOfType<Grid>());        }        public void Start()        {            Game.Start();        }        public void Update()        {            if (Game.GetGameStarted())            {                Game.Update();            }        }    }}