using System;using UnityEngine;using UnityEngine.EventSystems;namespace Memory.MonoBehaviours{    public class MemoryCard : MonoBehaviour    {        public event Action<int> Selected;        [SerializeField]        private SpriteRenderer _frameRenderer;        [SerializeField]        private SpriteRenderer _animalRenderer;        private int _pairIndex;        public void Init(int index, Sprite frame, Sprite animal)        {            _frameRenderer.sprite = frame;            _animalRenderer.sprite = animal;            _pairIndex = index;        }        public void OnMouseDown()        {            Selected?.Invoke(_pairIndex);        }    }}