using System;using Memory.Logic;using UnityEngine;namespace Memory.UserInterface{    public class UserInterface : MonoBehaviour    {        [SerializeField]        private UI_TopBar _topBar;        private void OnEnable()        {            Game.TimeLeftChanged += OnTimeChanged;            Game.ProgressChanged += OnProgressChanged;        }        private void OnDisable()        {            Game.TimeLeftChanged -= OnTimeChanged;            Game.ProgressChanged -= OnProgressChanged;        }        private void OnTimeChanged(TimeSpan timeLeft)        {            _topBar.OnTimeChanged(timeLeft: timeLeft);        }        private void OnProgressChanged(int current, int max)        {            _topBar.OnProgressChanged(current: current, max: max);        }    }}